INSERT INTO `tp_blog` VALUES (343, '马克思主义学院教师课堂教学质量考核暂行办法', '管理员', '<p style=\"text-indent:59px;line-height:150%\"><br/></p><p style=\"text-align:center;text-indent:52px;line-height:150%\"><strong><span style=\"font-size:21px;line-height:150%;font-family:仿宋_GB2312\">&nbsp;</span></strong></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">为加强课堂教学质量管理，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">促进教学改革，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">切实提高马克思主义学院教师教学水平，结合长期以来开展课堂教学质量检查和评价的经验，特制订此办法。</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">一、考核的目的</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">1.</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">为充分调动教师的积极性，推动教学水平进一步提高；</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">2.</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">为教学工作量计算中的教学质量系数定级提供依据；</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">3.</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">激励先进，促进教学管理的科学化、规范化，形成良好的教风。</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">二、考核的组织与管理</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">教师课堂教学质量的考核工作每学年进行一次，将本单位教师的课堂教学质量分成A（优秀）、B（良好）、C（合格）、D（不合格）四个等级。</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">三、考核对象</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">除省级教学名师外，承担授课任务的全体（专兼职）任课教师。省级学校另行考核。</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">四、考核办法</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">校级考核由同行评议、督导评价和学生评教三方构成，所占比重依次为4:3:3。</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">同行评议由学院组成评议组，对本单位教师的课堂教学质量进行打分（百分制）。</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">评议组成员根据课表随机听课，对同一任课教师至少听课4学时，按附表1“教师课堂教学质量评价表（专家用表）”为授课教师进行考核评价。</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">评价考核指标体系设四项一级指标，分别是教学态度、教学内容、教学方法和教学效果，各项所占比例分别是20%，30%，30%，20%。由考核者根据教师课堂教学状况进行评价打分。</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">学生评教由教务处提供。</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">督导评价由教学督导反馈评价。</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">学院评议组成员由班子成员、各教研室主任、教师代表组成</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">评议组组长：赵冰梅</span></p><p style=\"margin-left: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">成员：吕健、曲洪波、周琳娜、张澜、傅畅梅、李金华、岳宏杰、齐艳霞</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">六、考核结果及应用</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">1.</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">考核结果的具体统计办法</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">（1）最终认定等级为五级制：综合考核结果90分以上为A级，80-89分为B级，70-79分为C级，69分以下为D级。其中A级所占比例不超过学院专职教师总人数的15%</span></p><p style=\"text-indent: 47px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">2.</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">考核结果的应用</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">（1）考核结果是教师职称评审、教学评优的重要依据；</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">（2）考核结果是教师岗位评聘的重要依据；</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">（3）考核结果学院可纳入教学工作量的计算中；</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">（4）对于考核认定D级的教师，学院要帮助其查找原因，找出差距，及时进行培训和整改；情节严重者，及时停课，参加专职培训，考核合格后方能上岗。</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">七、本暂行办法自下发之日起执行，马克思主义学院教学质量评议小组负责解释。 </span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"text-indent: 429px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">马克思主义学院</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;2015</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年10月</span></p><p style=\"line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p><br/></p>', 1514172539, 100, 6, 0);
INSERT INTO `tp_blog` VALUES (316, '周琳娜', '管理员', '<p style=\"text-align:center;text-indent:59px;line-height:150%\"><img src=\"/Public/Upload/image/20171225/1514169269103663.png\" title=\"1514169269103663.png\" alt=\"1514169269103663.png\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">周琳娜，女，</span><span style=\"font-size:19px;line-height:150%\">1980</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:19px;line-height:150%\">9</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">月生，辽宁锦州人，中共党员，博士后，副教授，硕士研究生导师，辽宁省百千万人才万层次人才，现任马克思主义学院副院长。主要研究方向马克思主义中国化、思想政治教育。主讲《毛泽东思想和中国特色社会主义理论体系概论》《马克思主义中国化与文化现代化》等课程。曾获校优秀教师等荣誉称号。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">近年来，出版专著</span><span style=\"font-size:19px;line-height:150%\">1</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">部，公开发表论文</span><span style=\"font-size:19px;line-height:150%\">15</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">余篇。主持、参与省部级、市厅级等课题研究</span><span style=\"font-size:19px;line-height:150%\">20</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">余项。</span></p><p><br/></p>', 1514169184, 100, 16, 0);
INSERT INTO `tp_blog` VALUES (317, '张国良', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\"><img src=\"/Public/Upload/image/20171225/1514169347136126.jpg\" title=\"1514169347136126.jpg\" alt=\"1514169347136126.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">张国良，男，</span><span style=\"font-size:19px;line-height:150%\">1964</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:19px;line-height:150%\">10</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">月生，吉林人，中共党员，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">教授，</span><span style=\"font-size: 19px;line-height:150%;font-family:宋体\">硕士研究生导师</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">。现任学生处处长，主要研究方向思想政治教育，主讲《思想政治教育学》等课程。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">近年来，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体;color:#333333\">主编、主审专著3部，公开发表论文40余篇，其中被三大检索论文9篇，中文核心期刊论文18篇；主持、参与国家级、省部级、市厅级等课题30余项。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体;color:#333333\">其他荣誉称号:近年来获辽宁省大学生思想政治教育活动2项;辽宁省高校校园文化建设优秀成果奖2项; 沈阳高校大学生思想政治教育工作创新奖2项,所带团队曾获2015年辽宁省教育先进集体。</span></p><p><br/></p>', 1514169323, 100, 16, 0);
