INSERT INTO `tp_blog` VALUES (392, '关于组织开展2015年校级教学改革研究立项项目  结题验收工作的通知', '管理员', '<p style=\"text-align:center;line-height:150%\"><br/></p><p style=\"line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">校内有关单位：</span></p><p style=\"line-height:150%\"><span style=\"font-size: 19px;line-height:150%\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">根据《沈阳航空航天大学教改立项管理办法（修订）》（校发〔</span><span style=\"font-size:19px;line-height:150%\">2015</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">〕</span><span style=\"font-size:19px;line-height:150%\">10</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">号），学校决定组织开展</span><span style=\"font-size:19px;line-height:150%\">2015</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年校级教学改革研究立项项目结题验收工作，现将有关事项通知如下：</span></p><p style=\"line-height:150%\"><span style=\"font-size: 19px;line-height:150%\">&nbsp;<strong>&nbsp; </strong></span><strong><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">一、范围</span></strong></p><p style=\"line-height:150%\"><span style=\"font-size: 19px;line-height:150%\">&nbsp;&nbsp; </span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">通过中期检查的</span><span style=\"font-size:19px;line-height:150%\">2015</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年校级教学改革研究立项项目（见附件</span><span style=\"font-size:19px;line-height:150%\">1</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">）和</span><span style=\"font-size:19px;line-height:150%\">2014</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年度校级教学改革研究延期结题项目（见附件</span><span style=\"font-size:19px;line-height:150%\">2</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">），其中被确定为</span><span style=\"font-size:19px;line-height:150%\">2016</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年重点立项的项目暂不验收，将按照</span><span style=\"font-size:19px;line-height:150%\">2016</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年项目相关要求进行结题验收工作。</span></p><p style=\"line-height:150%\"><span style=\"font-size: 19px;line-height:150%\">&nbsp;&nbsp; <strong>&nbsp;</strong></span><strong><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">二、验收办法及要求</span></strong></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">本次验收委托项目推荐单位组织实施，具体要求如下：</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">1.</span><span style=\"font-size:19px;font-family:宋体\">项目推荐单位组建专家组负责验收工作，专家组不少于</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">人。</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">2.</span><span style=\"font-size:19px;font-family:宋体\">依据学校验收标准（附件</span><span style=\"font-size:19px\">4</span><span style=\"font-size:19px;font-family: 宋体\">），验收结果为通过验收、暂缓验收和不予验收。</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">3.</span><span style=\"font-size:19px;font-family:宋体\">项目推荐单位可根据项目实际研究情况，采取灵活多样的验收形式。</span></p><p style=\"text-indent:28px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:宋体\">三、时间安排</span></strong></p><p style=\"text-indent:28px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">项目推荐单位于</span><span style=\"font-size: 19px;line-height: 150%\">2017</span><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">年</span><span style=\"font-size: 19px;line-height: 150%\">11</span><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">月</span><span style=\"font-size: 19px;line-height: 150%\">22</span><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">日</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">前完成本单位立项项目验收工作，</span><span style=\"font-size:19px;line-height:150%\">2015</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年立项到应结题时间尚未完成需延期的项目，需提出延期申请，否则视为自动取消。延期只能申请一次，时间一般为</span><span style=\"font-size:19px;line-height:150%\">1</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年。</span><span style=\"font-size:19px;line-height:150%\">2014</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年校级教学改革研究延期结题项目本次不能结题将自动取消。</span></p><p style=\"margin-left:0;text-indent:37px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%\">四、</span></strong><strong><span style=\"font-size:19px;line-height:150%;font-family:宋体\">提交材料</span></strong></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">各单位须于</span><span style=\"font-size: 19px;line-height: 150%\">11</span><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">月</span><span style=\"font-size: 19px;line-height: 150%\">24</span><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">日前提交以下材料纸质版</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">（加盖公章）一式一份至行政楼</span><span style=\"font-size: 19px;line-height: 150%\">419</span><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">：</span></p><p style=\"margin-left:0;text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%\">1.</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">验收工作报告</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%\">2.</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">教改立项结题报表（附件</span><span style=\"font-size:19px;line-height:150%\">3</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">）</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">3.</span><span style=\"font-size:19px;font-family:宋体\">验收专家组名单（附件</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family: 宋体\">）</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">4.</span><span style=\"font-size:19px;font-family:宋体\">验收结果一览表（附件</span><span style=\"font-size:19px\">6</span><span style=\"font-size:19px;font-family: 宋体\">）</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">以上材料和项目结题相关支撑材料电子版发送至</span><span style=\"font-size:19px\">gaod197@163.com</span><span style=\"font-size:19px;font-family:宋体\">。</span></p><p style=\"text-indent:37px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:宋体\">五、联系方式</span></strong></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">联系人：高 丹</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">联系电话：18040030220</span></p><p style=\"text-indent:37px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:宋体\">附件</span></strong></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">附件1：2015年度校级教学改革研究立项项目名单</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">附件2：</span><span style=\"font-size:19px;line-height:150%\">2014</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年度校级教学改革研究延期结题项目</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">附件3：教改立项结题表</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">附件4：验收标准</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">附件5：</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">验收专家组名单</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">附件</span><span style=\"font-size: 19px;line-height:150%\">6</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">：验收结果一览表</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"margin-right:37px;text-align:center;text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">教务处</span></p><p style=\"text-align:right;text-indent:37px;line-height:150%\"><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">2017</span><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">年10月18日</span></p><p><br/></p>', 1514708711, 100, 2, 0);
