INSERT INTO `tp_blogss` VALUES (309, '郑晓娜', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513079197127374.jpg\" title=\"1513079197127374.jpg\" alt=\"1513079197127374.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p><span style=\"font-size:16px;font-family:宋体\">郑晓娜，女，&nbsp;1978年出生，副教授。2005年7月本硕毕业于东北师范大学政法学院思想政治教育专业，2015年7月辽宁大学马克思主义学院思想政治教育专业博士毕业。现任沈阳航空航天大学马克思主义学院德育教研室教师。主讲课程：思想道德修养与法律基础和形势与政策课程等。主要研究方向为大学生思想政治教育。曾先后在CSSCI\\北大核心期刊发表论文5篇，省级以上刊物发表论文20余篇，主持教育部省市科研课题10余项。</span></p><p><br/></p>', 1513079174, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (310, '周琳娜', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:21px;font-family:仿宋_GB2312\"><img src=\"/Public/Upload/image/20171212/1513079311946225.jpg\" title=\"1513079311946225.jpg\" alt=\"1513079311946225.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p><span style=\"font-size:21px;font-family:仿宋_GB2312\">周琳娜（1980—），辽宁锦州人。文学博士，思想政治教育专业博士后；现为马克思主义理论学科副教授、硕士研究生导师。主要研究方向为马克思主义中国化、思想政治教育；主要讲授“毛泽东思想和中国特色社会主义理论体系概论”、大学生通识教育等课程。曾主持教育部人文社会科学研究项目、辽宁省哲学社会科学规划基金项目等；出版学术著作2部；发表论文数篇。</span></p><p><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p><br/></p>', 1513079269, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (311, '仇梦欢', '管理员', '<p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">仇梦欢，女，</span><span style=\"font-size:19px;line-height:150%\">1980</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">年</span><span style=\"font-size:19px;line-height:150%\">9</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">月生，辽宁沈阳人，中共党员，法学硕士，现任马克思主义学院办公室主任</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">、直属党支部宣传委员、工会委员。</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">主要</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">负责办公室工作、研究生教务工作、工会工作等。</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">曾被评为校“三育人”标兵、共产党员先锋岗、优秀党务工作者、</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">优秀教职工党员、</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">研究生招生“先进个人”、</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\"> 优秀研究生管理干部、</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">工会工作优秀干部、</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">教职工协会优秀干部等荣誉称号。</span></p><p style=\"text-indent: 37px;line-height: 150%\"><span style=\"font-size:19px;line-height:150%;font-family:宋体\">先后</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">主持</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">沈阳市社会科学研究课题2项，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">校青年教师自选课题1项，并参与多项省社科基金、“十一五”规划、教育厅等课题研究，于2</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">012</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年度荣获沈阳市社会科学立项课题研究成果二等奖，2015年度荣获沈阳市社会科学立项课题研究成果三等奖，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">独立发表</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">管理类论文2篇。</span></p><p><br/></p>', 1513079358, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (312, '郭小伟', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513079465786233.jpg\" title=\"1513079465786233.jpg\" alt=\"1513079465786233.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p><span style=\"font-size:16px;font-family:宋体\">郭小伟，女，1961年出生，1981年毕业于辽宁教育学院艺术系，现任沈阳航空航天大学马克思主义学院副教授。主讲课程：艺术学概论、大学影视欣赏，中外影视经典欣赏、交响乐赏析、音乐基础知识与欣赏等。长期从事艺术教育方向、艺术教学研究。主要论文有：《艺术教育求实诌议》、《高校影视艺术审美教育的意义探索》、《音乐激发科学创造力》、《论普通高校的音乐审美教育》、《音乐教育和大学生非智力因素研究》、《音乐多媒体教学初探》等多篇。编著《艺术学概论》、《音乐名作欣赏》（合著）等。</span></p><p><br/></p>', 1513079381, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (313, '学院领导', '管理员', '<p style=\"text-indent:24px\"><strong><span style=\"font-size:16px;font-family:宋体\">&nbsp; &nbsp; &nbsp;&nbsp;</span></strong></p><p style=\"text-align:center\"><img src=\"/Public/Upload/image/20171212/1513080157103043.png\" title=\"1513080157103043.png\" alt=\"1513080157103043.png\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></p><p style=\"text-indent:24px\"><strong><span style=\"font-size:16px;font-family:宋体\"></span></strong><br/></p><p style=\"text-indent:24px\"><strong><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><strong><span style=\"font-size:19px;font-family: 楷体_GB2312\">院长&nbsp;&nbsp;&nbsp; 赵冰梅 教授</span></strong></p><p style=\"text-indent:75px;line-height:150%\"><span style=\";line-height:150%;font-family:宋体\">赵冰梅，院长，马克思主义学院直属党支部副书记。二级教授，硕士研究生导师。享受国务院政府特殊津贴，沈阳市优秀专家。获评教育育部高校思想政治理论课教师2014年度影响力人物、辽宁省教学名师、辽宁省优秀教师等；获得沈阳市五一劳动奖章、</span></p><p style=\"text-indent:75px;line-height:150%\"><span style=\";line-height:150%;font-family:宋体\">沈阳市劳动模范等多项荣誉称号。</span></p><p style=\"text-align:center\"><img src=\"/Public/Upload/image/20171212/1513080187739372.jpg\" title=\"1513080187739372.jpg\" alt=\"1513080187739372.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></p><p style=\"text-indent:101px;line-height:150%\"><strong><span style=\"font-size:19px;line-height: 150%;font-family:楷体_GB2312\">直属党支部书记&nbsp;&nbsp;&nbsp; 吕健&nbsp; 副教授</span></strong></p><p style=\"text-indent:21px;line-height:150%\"><span style=\";line-height:150%;font-family:宋体\">吕健，直属党支部书记兼副院长，硕士研究生导师。辽宁省优秀教师、辽宁省高校思想政治理论课教学名师、辽宁省普通高等学校优秀青年骨干教师、校教学名师。</span></p><p style=\"text-align:center\"><span style=\";line-height:150%;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513080221132296.jpg\" title=\"1513080221132296.jpg\" alt=\"1513080221132296.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-indent:131px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:楷体_GB2312\">副院长&nbsp; 曲洪波&nbsp; 副教授</span></strong></p><p><span style=\"font-size:16px;font-family:宋体\">曲洪波，副院长，历史学博士，硕士研究生导师。全国思政课教师年度影响力提名人物、辽宁省优秀思政课教师、沈阳市优秀教师、校教学名师、校大学生“我最喜爱的老师”。</span></p><p style=\"text-align:center\"><img src=\"/Public/Upload/image/20171212/1513080257460440.jpg\" title=\"1513080257460440.jpg\" alt=\"1513080257460440.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></p><p style=\"text-indent:131px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:楷体_GB2312\">副院长&nbsp; 周琳娜&nbsp; 副教授</span></strong></p><p style=\"text-align:center;text-indent:21px;line-height:150%\"><span style=\";line-height:150%;font-family:宋体\">周琳娜，副院长、文学艺术研室主任，副教授，文学博士，思想政治教育专业博士后，硕士研究生导师，主要负责学院科研工作。</span></p><p style=\"text-align:center\"><span style=\";line-height:150%;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513080438270669.jpg\" title=\"1513080438270669.jpg\" alt=\"1513080438270669.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-align:center;text-indent:27px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:楷体_GB2312\">办公室主任&nbsp; 仇梦欢</span></strong></p><p style=\"text-indent:24px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;&nbsp; </span><span style=\";line-height:150%;font-family:宋体\">仇梦欢，办公室主任，法学硕士，主要</span><span style=\";line-height:150%;font-family:宋体\">负责办公室工作、本科与研究生教务工作、工会工作等。</span><span style=\";line-height:150%;font-family:宋体\">曾被评为校“三育人”标兵、</span><span style=\";line-height:150%;font-family:宋体\">校研究生招生“先进个人”等称号。</span></p><p>&nbsp;</p><p><br/></p>', 1513079533, 100, 4, 0);
INSERT INTO `tp_blogss` VALUES (300, '田雪', '管理员', '<p style=\"text-align:center\"><strong><span style=\"font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513078465975401.jpg\" title=\"1513078465975401.jpg\" alt=\"1513078465975401.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></strong></p><p><strong><span style=\"font-family:宋体\">田雪</span></strong><span style=\"font-family: 宋体\">，女，</span>1982<span style=\"font-family:宋体\">年</span>8<span style=\"font-family:宋体\">月生，辽宁省沈阳市人，中共党员，职称：工程师</span></p><p><span style=\"font-family:宋体\">主讲课程：《大学生心理健康》</span></p><p><span style=\"font-family:宋体\">科研情况：主持、参与省级以上课题</span>10<span style=\"font-family:宋体\">余项，主持、参与科研、教改课题</span>3<span style=\"font-family:宋体\">项。公开出版教材</span>2<span style=\"font-family:宋体\">部。发表论文</span>10<span style=\"font-family:宋体\">余篇。多篇论文获辽宁省高等学校心理健康教育研究会奖励。</span></p><p>&nbsp;</p><p><br/></p>', 1513078439, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (301, '王旭', '管理员', '<p style=\"line-height:125%\"><span style=\"font-size: 16px;line-height:125%\">&nbsp;</span></p><p style=\"text-align:center\"><img src=\"/Public/Upload/image/20171212/1513078582606317.jpg\" title=\"1513078582606317.jpg\" alt=\"1513078582606317.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></p><p style=\"line-height:125%\"><span style=\"font-size: 16px;line-height:125%\"></span><br/></p><p style=\"line-height:125%\"><span style=\"font-size:16px;line-height:125%;font-family:宋体\">王旭，女，</span><span style=\"font-size:16px;line-height:125%\">1988</span><span style=\"font-size:16px;line-height:125%;font-family: 宋体\">年</span><span style=\"font-size:16px;line-height:125%\">1</span><span style=\"font-size:16px;line-height:125%;font-family: 宋体\">月，黑龙江省大庆市人。</span><span style=\"font-size:16px;line-height:125%\">2015</span><span style=\"font-size:16px;line-height:125%;font-family: 宋体\">年</span><span style=\"font-size:16px;line-height:125%\">7</span><span style=\"font-size:16px;line-height:125%;font-family: 宋体\">月毕业于东北大学马克思主义基本原理专业，获法学博士学位。现任沈阳航空航天大学马克思主义学院政治理论教研室讲师。主讲课程：毛泽东思想和中国特色社会主义理论体系概论、中国近现代史纲要。主要研究方向为马克思生态理论。曾获沈阳航空航天大学教师教学比赛三等奖。先后在省级以上期刊发表论文</span><span style=\"font-size:16px;line-height:125%\">6</span><span style=\"font-size:16px;line-height:125%;font-family: 宋体\">篇，参与国家社科基金项目，主持辽宁省教育厅项目、辽宁省社科基金项目。</span></p><p style=\"line-height:125%\"><span style=\"font-size: 16px;line-height:125%\">&nbsp;</span></p><p><br/></p>', 1513078523, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (302, '闫华', '管理员', '<p style=\"text-align:center\"><span style=\"font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513078621658988.jpg\" title=\"1513078621658988.jpg\" alt=\"1513078621658988.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p><span style=\"font-family:宋体\">闫华，女，</span>1973<span style=\"font-family:宋体\">年</span>2<span style=\"font-family:宋体\">月生，辽宁省沈阳市人，中共党员，副教授。</span></p><p><span style=\"font-family:宋体\">主讲课程：大学生心理健康、心理素质训练、积极心理学</span></p><p><span style=\"font-family:宋体\">科研情况：发表省级以上刊物论文</span>10<span style=\"font-family:宋体\">余篇，主持校级教改课题</span>2<span style=\"font-family:宋体\">项，参加省级课题</span>5<span style=\"font-family:宋体\">项。</span></p><p><br/></p>', 1513078612, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (303, '岳宏杰', '管理员', '<p style=\"text-align:center\"><img src=\"/Public/Upload/image/20171212/1513078720126027.jpg\" title=\"1513078720126027.jpg\" alt=\"1513078720126027.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></p><p style=\"text-indent:32px;line-height:150%\">岳宏杰，女，1978年1月生，辽宁省锦州市人，中共党员，讲师，德育教研室主任。</p><p style=\"text-indent:32px;line-height:150%\">主讲课程：思想道德修养与法律基础、毛泽东思想与中国特色社会主义理论体系概论、形势与政策</p><p style=\"text-indent:32px;line-height:150%\">所获荣誉：2008年和2012年先后荣获沈阳市高校青年教师教学基本功大赛“教学能手”和“优秀选手”称号。2013年荣获辽宁省高校思想政治理论课精彩多媒体课件《形势与政策》二等奖和“中国梦&nbsp;我的梦”第六届沈阳高校形势与政策教育教案设计大赛二等奖，2015年荣获沈阳高校形势与政策授课一等奖，2016年荣获第五届全省高校思想政治理论课青年教师教学大赛一等奖。</p><p style=\"text-indent:32px;line-height:150%\">科研情况：主要研究方向为思想政治教育。曾主持辽宁省社科基金项目2项、辽宁省教育厅课题1项、辽宁省教育科学“十二五”规划课题1项、辽宁省高校健康管理协同创新中心资助项目2015年应急课题项目1项、沈阳市社会科学研究课题1项、校思政课题1项、校青年教师自选课题1项、校教改课题2项，并参与了辽宁省教育厅和辽宁省社科联课题多项。其中校级教改课题“《当代世界经济与政治》教材建设与专题教学模式研究”获校教学成果三等奖。先后在省级以上刊物发表论文7篇，出版著作1部，参编教材及教辅材料2部。</p><p><br/></p>', 1513078695, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (304, '张彩霞', '管理员', '<p style=\"text-align:center\"><span style=\"font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513078834133006.jpg\" title=\"1513078834133006.jpg\" alt=\"1513078834133006.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p><span style=\"font-family:宋体\">张彩霞，女，</span>1971<span style=\"font-family:宋体\">年</span>3<span style=\"font-family: 宋体\">月生，辽宁省抚顺市人，马克思主义学院副教授。</span></p><p><span style=\"font-family:宋体\">主讲课程：大学语文、演讲与口才、应用文写作、形势与政策等。</span></p><p><span style=\"font-family:宋体\">所获荣誉：</span>2006<span style=\"font-family:宋体\">年和</span>2013<span style=\"font-family: 宋体\">年，分别获得沈阳航空航天大学优秀青年教师和优秀教师称号。</span></p><p><span style=\"font-family:宋体\">科研情况：任现职以来发表论文</span>20<span style=\"font-family:宋体\">余篇，主编或参编教材</span>3<span style=\"font-family:宋体\">部，主持并参与各类课题</span>10<span style=\"font-family:宋体\">余项。</span></p><p>&nbsp;</p>', 1513078739, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (305, '张澜', '管理员', '<p style=\"text-align:center\"><img src=\"/Public/Upload/image/20171212/1513078884129826.jpg\" title=\"1513078884129826.jpg\" alt=\"1513078884129826.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">张澜，女，出生于1968年11月，辽宁清原人。现任沈阳航空航天大学马克思主义学院心理研究所所长、教授、硕士生导师，主讲“大学生心理健康”课程。担任“辽宁省高等学校心理健康教育研究会”常务理事、“沈阳市心理学会”常务理事。</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">多年来，获得多项荣誉： “校教学名师”、“辽宁省高等学校优秀青年骨干教师”， “辽宁省高等学校心理健康教育工作先进个人”、 “辽宁省高校心理健康教育骨干教师”、 “辽宁省高校思想政治理论课教师‘结对子’活动先进个人”、 “辽宁省高等学校优秀</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">班主任（导师）</span><span style=\"font-size: 16px;line-height: 150%;font-family: 宋体\">标兵”称号。</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">。</span></p><p style=\"margin-left:32px;text-indent:8px;line-height:32px\"><span style=\"font-size:16px;font-family:宋体\">科研情况：教学工作扎扎实实，走在前列，科研成果丰富。近年来，她先</span></p><p style=\"line-height:32px\"><span style=\"font-size:16px;font-family:宋体\">后发表论文60余篇，出版著作（教材）10部；主持和参与了教育部、辽宁省社科基金、辽宁省教育厅、辽宁省社科联----</span><span style=\"font-size:16px;font-family:宋体\">“辽宁居民生活水平地区差异研究</span><span style=\"font-size:16px;font-family:宋体\">（L07BSH015）”、“加强大学生心理健康教育的对策（L12DZZ059）”、“宗教对大学生影响问题（08WD2172）”等课题50余项。专著《思想政治教育的心理基础》2010年被评为“辽宁省高等学校加强和改进大学生思想政治教育优秀专著三等奖”。</span></p><p style=\"text-indent:36px;line-height:32px\"><span style=\"font-size:16px;font-family:宋体\">作为“沈航大学生心理咨询中心”负责人，常年领导大学生的心理健康教育和心理咨询工作，主持大一新生的心理测试工作并进行心理筛查，避免了一些极端心理问题的发生。2007年我校被评为“辽宁省高等学校心理健康教育工作先进单位”。</span></p><p style=\"margin-left:1px;text-indent:32px;line-height:32px\"><span style=\"font-size:16px;font-family:宋体\">她主持</span><span style=\"font-size:16px;font-family: 宋体\">的教改项目---“发展和完善大学生心理健康教育体系的探索与实践”、“以课堂教学为本，以心理实训为依托的大学生心理健康教育多元化模式构建与实践” 、“新形势下大学生职业生涯教育与就业指导的理论与实践研究”2006年、2012年、2008年和2016年分别获得“沈航教学成果一等奖、二等奖和三等奖”。参与的教改项目“新形势下高校大学生思想政治教育方式、方法的改革与探索”</span><span style=\"font-size:16px;font-family:宋体\">2009</span><span style=\"font-size:16px;font-family: 宋体\">年获得“辽宁省教学成果三等奖”。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">。</span></p><p><br/></p>', 1513078861, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (306, '张晓茜', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513078951859387.jpg\" title=\"1513078951859387.jpg\" alt=\"1513078951859387.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p><span style=\"font-size:16px;font-family:宋体\">张晓茜，1977年12月生人，辽宁省阜新市人，群众，讲师。<br/> 课程：音乐基础知识与赏析。<br/> 荣誉：2005年获得教育部艺术教育委员会颁发的“优秀教师奖”。2012年获得辽宁省音乐家协会颁发的“优秀指导教师”奖。<br/> 科研：多次在《吉林省教育学院学报》、《新课程》、《新课程学习》等期刊中发表论文。<br/> <br/> <br/> </span></p><p>&nbsp;</p><p><br/></p>', 1513078935, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (307, '赵冰梅', '管理员', '<p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\"><img src=\"/Public/Upload/image/20171212/1513079010105931.png\" title=\"1513079010105931.png\" alt=\"1513079010105931.png\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">赵冰梅，女，</span><span style=\"font-size:19px;line-height:150%\">1962</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:19px;line-height:150%\">7</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">月生，辽宁沈阳人，中共党员，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">二级教授，享受国务院政府特殊津贴</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">，硕士研究生导师</span><span style=\"font-size: 19px;line-height:150%;font-family:宋体\">。</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">主要研究方向马克思主义中国化，主讲《毛泽东思想和中国特色社会主义理论体系概论》《当代社会思潮》等课程。</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">现任马克思主义学院院长。</span><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">辽宁省高校思想政治理论课学科带头人、辽宁省省级教学团队、省级精品资源共享课带头人；</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">辽宁省高校思想政治理论教育研究会“毛泽东思想和中国特色社会主义理论概论”课专业委员会主任委员、辽宁省马克思主义研究会副理事等。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">获得教育部</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">高校思想政治理论课教师</span><span style=\"font-size: 19px;line-height:150%\">2014</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年度影响力人物</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">、辽宁省教学名师、辽宁省优秀教师、沈阳市优秀专家、</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">沈阳市五一劳动奖章、沈阳市劳动模范等多项荣誉称号。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">近年来，出版专著</span><span style=\"font-size:19px;line-height:150%\">10</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">部，参编著作</span><span style=\"font-size:19px;line-height:150%\">10</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">部，公开发表论文</span><span style=\"font-size:19px;line-height:150%\">40</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">篇。主持国家级、省部级、市厅级等课题</span><span style=\"font-size:19px;line-height:150%\">50</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">余项。获国家级高等教育教学成果二等奖</span><span style=\"font-size:19px;line-height:150%\">1</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">项，教育部高校思想政治理论课教学方法改革“择优推广计划”入选项目</span><span style=\"font-size:19px;line-height:150%\">1</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">项，辽宁省哲学社会科学成果奖三等奖</span><span style=\"font-size:19px;line-height:150%\">1</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">项，辽宁省教学成果奖多项。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%\">&nbsp;</span></p><p><br/></p>', 1513078983, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (299, '田克', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513078393641087.jpg\" title=\"1513078393641087.jpg\" alt=\"1513078393641087.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"margin-bottom: 30px;text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">田克，女，1973年1月生，辽宁省昌图市人，中共党员，副教授。</span></p><p style=\"margin-bottom: 30px;text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">主讲课程：《思想道德修养与法律基础》、《形势与政策》、《当代世界经济与政治》。</span></p><p style=\"margin-bottom: 30px;text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">所获荣誉：曾获得第四届辽宁省高校思想政治理论课青年教师教学大赛一等奖，“沈阳高校形势与政策教育公开课大赛”一等奖等。曾被授予“辽宁省高等学校思想政治理论课教学能手”称号、“辽宁高校思想政治理论课教师‘结对子’活动先进个人”荣誉称号。2014年和2015年连续两年获得沈阳航空航天大学“我最喜爱的十佳教师” 荣誉称号。</span></p><p style=\"margin-bottom: 30px;text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">科研成果：参与的“辽宁省文化创意产业发展问题研究”课题研究成果获得辽宁省第十一届哲学社会科学成果奖一等奖。几年来主持省社科基金等课题近10项，参与课题10余项，其中在各类期刊发表学术论文近20篇。</span></p><p><br/></p>', 1513078338, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (314, '党政联席会议制度', '管理员', '<p></p><p style=\"line-height: 16px;\"><img style=\"vertical-align: middle; margin-right: 2px;\" src=\"/web/marx/Application/Admin/View/Public/Js/ueditor/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a style=\"font-size:12px; color:#0066cc;\" href=\"/Public/Upload/file/20171212/1513092737262020.doc\" title=\"1.党政联席会议制度.doc\">1.党政联席会议制度.doc</a></p><p><br/></p>', 1513092640, 100, 18, 0);
INSERT INTO `tp_blogss` VALUES (286, '白蔚', '管理员', '<p style=\"text-align:center\"><span style=\"font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513073721539317.jpg\" title=\"1513073721539317.jpg\" alt=\"1513073721539317.jpg\" width=\"560\" height=\"889\" style=\"width: 560px; height: 889px;\" border=\"0\" vspace=\"0\"/></span></p><p><span style=\"font-family:宋体\">白蔚，</span>1971<span style=\"font-family:宋体\">年生，女，博士，副教授。曾先后在辽宁社会科学院、上海大学从事科研与教学工作，现于沈阳航空航天大学马克思主义学院任专职教师。主要研究方向：文化哲学、传媒社会学与社会性别研究。主要研究成果：《</span><a href=\"http://kns.cnki.net/kns/detail/detail.aspx?QueryID=0&CurRec=9&DbCode=CJFQ&dbname=CJFD2001&filename=FNYJ200102005&urlid=&yx=\" target=\"_blank\"><span style=\"font-family: 宋体;color: black\">中国电影的女性母题及其现代意蕴</span></a><span style=\"font-family: 宋体\">》《</span><a href=\"http://kns.cnki.net/kns/detail/detail.aspx?QueryID=0&CurRec=2&DbCode=CJFQ&dbname=CJFD2010&filename=ZZXK201005033&urlid=&yx=\" target=\"_blank\"><span style=\"font-family: 宋体;color: black\">改革开放以来中国女性消费身体的现代性悖论</span></a><span style=\"font-family:宋体\">》《</span><a href=\"http://kns.cnki.net/kns/detail/detail.aspx?QueryID=0&CurRec=4&DbCode=CJFQ&dbname=CJFD2006&filename=SHKK200603005&urlid=&yx=\" target=\"_blank\"><span style=\"font-family: 宋体;color: black\">三位“马克斯”对资本主义精神的解读</span></a><span style=\"font-family:宋体\">》</span><a href=\"http://kns.cnki.net/kns/detail/detail.aspx?QueryID=0&CurRec=1&DbCode=CJFQ&dbname=CJFD2011&filename=FNYJ201104011&urlid=&yx=\" target=\"_blank\"><span style=\"font-family: 宋体;color: black\">《</span><span style=\"font-family: 宋体;color: black\">摩登与反摩登——民国报刊建构的女性身体及其现代意义</span></a><span style=\"font-family:宋体\">》《传媒中的女性角色与现代性（</span>1900-1999<span style=\"font-family:宋体\">）》等论著。</span></p><p><span style=\"font-size:9px;font-family:&#39;Arial&#39;,sans-serif;color:#222222\">&nbsp;</span></p><p>&nbsp;</p><p><br/></p>', 1513073664, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (287, '傅畅梅', '管理员', '<p style=\"text-align:center;line-height:150%\"><br/></p><p style=\"line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%\">&nbsp;</span></strong></p><p style=\"text-align:center\"><img src=\"/Public/Upload/image/20171212/1513076341138626.jpg\" title=\"1513076341138626.jpg\" alt=\"1513076341138626.jpg\" width=\"400\" height=\"400\" style=\"width: 400px; height: 400px;\" border=\"0\" vspace=\"0\"/></p><p style=\"line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%\"></span></strong><br/></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">傅畅梅，女，</span></strong><strong><span style=\"font-size:16px;line-height:150%\">1972</span></strong><strong><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年</span></strong><strong><span style=\"font-size:16px;line-height:150%\">6</span></strong><strong><span style=\"font-size:16px;line-height:150%;font-family:宋体\">月生，辽宁大连人，中共党员，副教授，哲学博士，硕士研究生导师，马克思主义基本原理教研室主任。</span></strong></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">主讲《马克思主义基本原理概论与实践》、《毛泽东思想与中国特色社会主义理论概论与实践》、《马克思主义与当代科技》等课程。</span></strong></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">曾获辽宁省普通高校优秀青年骨干教师，校优秀教师，校优秀科技工作者，校“三育人”优秀个人等荣誉称号。</span></strong></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">现为中国自然辩证法研究会环境哲学与环境伦理专业委员会理事，中国自然辩证法研究会技术哲学专业委员会理事，辽宁省自然辩证法研究会常务理事，辽宁省哲学研究会理事。</span></strong></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">现主要从事科学、技术与社会方面和马克思主义理论等方向的基本理论和应用方面的研究。</span></strong></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">近年来，先后在核心期刊《哲学研究》、《自然辩证法研究》等刊物上发表学术论文</span></strong><strong><span style=\"font-size:16px;line-height:150%\">20</span></strong><strong><span style=\"font-size:16px;line-height:150%;font-family:宋体\">余篇，有的被《新华文摘》、《人大复印资料》转载。</span></strong></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">已出版个人学术专著一部，参研著作一部。</span></strong></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">主持辽宁省社科基金项目、经济社会发展课题多项，并作为执行负责人参与了国家社科基金项目。</span></strong></p><p style=\"text-indent:32px;line-height:150%\"><strong><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">获第六届高等学校科学研究优秀成果奖</span></strong><strong><span style=\"font-size:16px;line-height:150%\">(</span></strong><strong><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人文社会科学</span></strong><strong><span style=\"font-size:16px;line-height:150%\">)</span></strong><strong><span style=\"font-size:16px;line-height:150%;font-family:宋体\">二等奖</span></strong><strong><span style=\"font-size:16px;line-height:150%\">1</span></strong><strong><span style=\"font-size:16px;line-height:150%;font-family:宋体\">次，获省级哲学社会科学研究成果一等奖</span></strong><strong><span style=\"font-size:16px;line-height:150%\">1</span></strong><strong><span style=\"font-size:16px;line-height:150%;font-family:宋体\">次，获市级哲学社会科学成果奖多次。</span></strong><strong> </strong></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%\">&nbsp;</span></p><p><br/></p>', 1513076162, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (288, '姜爱华', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513076526106589.jpg\" title=\"1513076526106589.jpg\" alt=\"QQ图片20171110135920.jpg\"/></span></p><p><span style=\"font-size:16px;font-family:宋体\">姜爱华，女，朝鲜族，1975年生，辽宁沈阳市人。1998年毕业于沈阳师范大学思想政治教育专业，2003年硕士毕业于辽宁大学西方哲学专业，2006年博士毕业于北京师范大学马克思主义哲学专业。2006年至今在沈阳航空航天大学马克思主义学院工作，多年来一直从事马克思主义基础理论方面的教学与研究，在《探索》、《毛泽东思想研究》、《理论探索》、《辽宁大学学报》等全国中文核心期刊上发表专业学术论文十余篇，出版专著、参编教材各一部，主持并参与多项省市级科研课题。2015年受国家留学基金委资助在英国牛津大学访学一年。</span></p>', 1513076505, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (289, '金梦兰', '管理员', '<p><span style=\"font-family:宋体\"></span></p><p style=\"text-align:center\"><span style=\"font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513077218154441.png\" title=\"1513077218154441.png\" alt=\"1513077218154441.png\" width=\"400\" height=\"889\" border=\"0\" vspace=\"0\" style=\"width: 400px; height: 889px;\"/></span></p><p><span style=\"font-family: 宋体;\">金梦兰，女，</span>1977<span style=\"font-family: 宋体;\">年生，辽宁省海城市人，中共党员，职称讲师。</span></p><p><span style=\"font-family:宋体\">主讲课程：马克思主义基本原理概论、当代世界经济与政治等。</span></p><p><span style=\"font-family:宋体\">科研情况：出版专著一部，译著两部，参编教材多部，历年来共在省级以上刊物发表论文十余篇，主持和参与省、市、校级的科研及教研课题多项。</span></p><p><br/></p>', 1513076620, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (290, '李金华', '管理员', '<p style=\"text-align: center;\"><img src=\"/Public/Upload/image/20171212/1513077306458804.jpg\" title=\"1513077306458804.jpg\" alt=\"1513077306458804.jpg\" width=\"400\" height=\"340\" style=\"width: 400px; height: 340px;\" border=\"0\" vspace=\"0\"/></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">李金华，女，1964年2月出生，辽宁沈阳人，中共党员，副教授，硕士生导师。</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">主讲《思想道德修养与法律基础》、《形势与政策》、《德治与法治教育研究》三门课程。</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">曾获得校“优秀教师”、“三育人先进个人”、“先进科技工作者”、“优秀共产党员”“优秀党务工作者”和“辽宁省高校思政教师‘结对子’先进个人”等荣誉称号。</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">科研：获得辽宁省政府成果三等奖1项；辽宁省教学成果一等奖1项、三等奖2项；校教学成果一等奖3项、三等奖1项；辽宁省教育厅“两课”教学理论研究成果、沈阳市社会科学优秀学术成果等各类成果奖10余项。近年来，发表省级以上学术论文30余篇，其中全国中文核心期刊10篇；主持完成各级各类课题9项，其中省社科基金课题2项；参与课题30余项；出版《心河安渡》、《中国民俗文化学概论》、《高校思想政治教育理论研究述评》、《大学生思想政治教育环境学创新研究》、《荣辱的力量》、《思想政治理论课情景剧教学法研究》著作5部，主编和参编《形势与政策》教材和《思想道德修养与法律基础综合实践用书》6本。</span></p><p><br/></p>', 1513077273, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (291, '李莹', '管理员', '<p style=\"text-align:center\"><span style=\"font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513077552702838.jpg\" title=\"1513077552702838.jpg\" alt=\"1513077552702838.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p><span style=\"font-family:宋体\">李莹，女，</span>1963<span style=\"font-family:宋体\">年</span>6<span style=\"font-family: 宋体\">月生，吉林省公主岭市人，中共党员，副教授。现任沈阳航空航天大学马克思主义学院思想政治理论教研室教师。</span>1986<span style=\"font-family:宋体\">年毕业于东北师范大学历史系，</span>2006<span style=\"font-family:宋体\">年获东北师范大学法学硕士学位，主讲课程：《中国近现代史纲要》、《毛泽东思想和中国特色社会主义理论体系概论》、《民俗与异国风情》《中国近现代政党史》等课程。主要研究方向为历史和思想政治教育。历年来共在省级刊物发表论文</span>20<span style=\"font-family:宋体\">余篇，主持或参与省级、市级课题</span>10<span style=\"font-family:宋体\">多项，主编校内教材</span>1<span style=\"font-family:宋体\">部，曾有多篇论文及“多媒体课件”在省、市获奖。教学理念授之以理，投之以情，认真讲好每一堂课，爱护每一个学生。</span></p><p><br/></p>', 1513077529, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (292, '刘春霞', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\"><img src=\"/Public/Upload/image/20171212/1513077692903667.jpg\" title=\"1513077692903667.jpg\" alt=\"1513077692903667.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">刘春霞，女，</span><span style=\"font-size:19px;line-height:150%\">1973</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:19px;line-height:150%\">1</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">月生，黑龙江省克东县人，副教授。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">主讲课程：《中国近现代史纲要》《毛泽东思想和中国特色社会主义理论体系概论》《中国传统文化概论》</span> <span style=\"font-size:19px;line-height:150%;font-family:宋体\">。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">所获荣誉：沈阳航空航天大学优秀青年教师</span> <span style=\"font-size:19px;line-height:150%;font-family:宋体\">。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">科研情况：在《辽宁大学学报》《辽宁师范大学学报》等发表论文</span><span style=\"font-size:19px;line-height:150%\">20</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">余篇、参编著作</span><span style=\"font-size:19px;line-height:150%\">2</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">部，主持、参与各类科研项目十余项。</span></p><p><br/></p>', 1513077597, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (293, '卢海燕', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\"><img src=\"/Public/Upload/image/20171212/1513077772863002.jpg\" title=\"1513077772863002.jpg\" alt=\"1513077772863002.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">卢海燕，女，</span><span style=\"font-size:19px;line-height:150%\">1971</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:19px;line-height:150%\">4</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">月生，辽宁省铁岭市人，中共党员，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">副教授</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">主讲课程：毛泽东思想和中国特色社会主义理论体系概论，中国近现代史纲要，当代世界经济与政治。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">所获荣誉：第二届辽宁省思想政治理论课青年教师教学大赛二等奖，辽宁省高校思想政治理论课教师“精彩教案”一等奖。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">科研情况：发表相关学术论文多篇、参与多部著作编写，辽宁省第</span><span style=\"font-size:19px;line-height:150%\">5</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">届社会科学学术活动月“第二届当代中国马克思主义论坛”二等奖，曾获沈阳市社会科学优秀学术成果优秀奖。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%\">&nbsp;</span></p><p><br/></p>', 1513077751, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (294, '吕健', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\"><img src=\"/Public/Upload/image/20171212/1513077891833525.jpg\" title=\"1513077891833525.jpg\" alt=\"1513077891833525.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">吕健，女，</span><span style=\"font-size:19px;line-height:150%\">1968</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:19px;line-height:150%\">4</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">月生，辽宁省大连市人，中共党员，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">副教授</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">，硕士研究生导师，马克思主义学院直属党支部书记兼副院长。主讲课程：中国近现代史纲要、毛泽东思想和中国特色社会主义理论体系概论、马克思主义中国化基本问题研究。主要研究方向：马克思主义中国化和党史党建。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">先后获得辽宁省思政课教学名师、辽宁省优秀教师、辽宁省高校思想政治理论课教师“结对子”活动先进个人称号。获得校教学名师、校优秀共产党员、校优秀党务工作者、校优秀硕士生导师、校优秀科技工作者等称号。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">近年来，出版专著</span><span style=\"font-size:19px;line-height:150%\">4</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">部，参编著作近</span><span style=\"font-size:19px;line-height:150%\">10</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">部，公开发表论文</span><span style=\"font-size:19px;line-height:150%\">30</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">余篇。主持国家级、省部级、市厅级课题</span><span style=\"font-size:19px;line-height:150%\">20</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">余项，参与国家级、省部级、市厅级课题</span><span style=\"font-size:19px;line-height:150%\">30</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">余项。获得辽宁省</span><span style=\"font-size:19px;line-height:150%\">2011-2012</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年度哲学社会科学成果奖三等奖（</span><span style=\"font-size:19px;line-height:150%\">2016</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年）、辽宁省教学成果一等奖。</span></p><p><br/></p>', 1513077849, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (295, '马新凤', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:19px;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513077977156162.png\" title=\"1513077977156162.png\" alt=\"1513077977156162.png\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">马新凤，女，汉族，1963年</span><span style=\"font-size:19px\">1</span><span style=\"font-size:19px;font-family:宋体\">月生人。辽宁省沈阳市人。中共党员，职称，副教授，现任沈阳航空航天大学马克思主义学院德育教研室教师。</span></p><p><span style=\"font-size:19px;font-family:宋体\">主讲课程：《思想道德修养与法律基础》、《形势与政策》。</span></p><p><span style=\"font-size:19px;font-family:宋体\">专业领域为思想政治教育，主要研究方向为思想政治教育。</span></p><p><span style=\"font-size:19px;font-family:宋体\">科研情况：</span></p><p><span style=\"font-size:19px;font-family: 宋体\">1</span><span style=\"font-size:19px;font-family:宋体\">、任职以来，共发表《关于新农村建设的几点思考》等3篇中文核心期刊；</span></p><p><span style=\"font-size:19px;font-family: 宋体\">2</span><span style=\"font-size:19px;font-family:宋体\">、在省级以上刊物发表论文《培育人文素质，提升人文精神》等</span><span style=\"font-size:19px\">20</span><span style=\"font-size: 19px;font-family:宋体\">余篇；</span></p><p><span style=\"font-size:19px;font-family: 宋体\">3</span><span style=\"font-size:19px;font-family:宋体\">、主持或参与科研及教研课题近</span><span style=\"font-size:19px\">10</span><span style=\"font-size: 19px;font-family:宋体\">多项。如：《关于提高沈阳市干部队伍贯彻落实科学发展观能力的对策研究》等。</span></p><p><span style=\"font-size:19px;font-family: 宋体\">4</span><span style=\"font-size:19px;font-family:宋体\">、</span><span style=\"font-size:19px\">2009</span><span style=\"font-size: 19px;font-family:宋体\">年开始担任《形势与政策》课程负责人。</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">曾有多篇论文及“多媒体课件”获奖，所负责课程曾获辽宁省思想政治理论课优秀教学团队称号。个人曾被评为校工会积极分子。</span></p><p><br/></p>', 1513077915, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (296, '欧阳辉', '管理员', '<p style=\"text-align:center\"><span style=\"font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513078035117833.jpg\" title=\"1513078035117833.jpg\" alt=\"1513078035117833.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p><span style=\"font-family:宋体\">欧阳辉</span><span style=\"font-family: 宋体\">，男，</span>1958<span style=\"font-family:宋体\">年</span>3<span style=\"font-family:宋体\">月生，辽宁省沈阳市人，中共党员，职称：副教授</span></p><p><span style=\"font-family:宋体\">主讲课程：《大学生心理健康》、《社会心理学》、《管理心理学》</span></p><p><span style=\"font-family:宋体\">所获荣誉：主持教改成果获“校教改成果”二等奖。</span>2009<span style=\"font-family:宋体\">年“社会心理学”课件获“辽宁省第十届软件大赛三等奖”，“校软件大赛”三等奖。</span></p><p><span style=\"font-family:宋体\">科研情况：近年来，主持、参与省级以上课题</span>10<span style=\"font-family:宋体\">余项，主持横向课题</span>5<span style=\"font-family:宋体\">项，争取科研经费</span>50<span style=\"font-family:宋体\">万元。公开出版著作</span>4<span style=\"font-family:宋体\">部、教材</span>4<span style=\"font-family:宋体\">部。发表论文</span>30<span style=\"font-family:宋体\">余篇，</span>ISTP<span style=\"font-family:宋体\">检索论文</span>5<span style=\"font-family:宋体\">篇。多篇论文获辽宁省高等学校心理健康教育研究会奖励。</span></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p><br/></p>', 1513078002, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (297, '齐艳霞', '管理员', '<p style=\"text-align:center\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\"><img src=\"/Public/Upload/image/20171212/1513078188129999.png\" title=\"1513078188129999.png\" alt=\"1513078188129999.png\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">齐艳霞，女，</span><span style=\"font-size:19px;line-height:150%\">1976</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:19px;line-height:150%\">4</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">月生，山东省聊城市人，中共党员，</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">讲师</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">，博士，马克思主义中国化教研室主任。</span></p><p style=\"text-indent:37px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:&#39;微软雅黑&#39;,sans-serif\">主讲课程：</span></strong><span style=\"font-size:19px;line-height:150%;font-family:宋体\">马克思主义基本原理；毛泽东思想和中国特色社会主义理论体系概论；社会主义核心价值观研究</span></p><p style=\"text-indent:37px\"><strong><span style=\"font-size:19px;font-family: &#39;微软雅黑&#39;,sans-serif\">所获荣誉：</span></strong></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省第五届高校青年教师教学大赛</span> <span style=\"font-size:19px;font-family: 宋体\">一等奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省第五届高校青年教师教学大赛“我最喜爱的老师”</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">沈阳市第六届高校青年教师教学基本功大赛优秀教学能手奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省第十二届教育软件大赛</span> <span style=\"font-size:19px;font-family: 宋体\">二等奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省教育软件大赛</span> <span style=\"font-size:19px;font-family: 宋体\">三等奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省第十八届信息化大赛</span> <span style=\"font-size:19px;font-family: 宋体\">二等奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省高校思想政治理论课教学能手</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省高校思想政治理论课“精彩微课”二等奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">教学成果一等奖政治理论课资源共享课建设的探索与实践</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省首届高校思想政治理论课青年教师教学大赛</span> <span style=\"font-size:19px;font-family: 宋体\">二等奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">辽宁省高校思想政治理论课精彩教案</span> <span style=\"font-size:19px;font-family: 宋体\">三等奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">沈阳航空航天大学人文社科部教学软件大赛三等奖</span></p><p style=\"margin-left:62px\"><span style=\"font-size:19px;font-family: Wingdings\">u </span><span style=\"font-size:19px;font-family:宋体\">沈阳航空航天大学</span><span style=\"font-size:19px\">2013</span><span style=\"font-size:19px;font-family: 宋体\">、</span><span style=\"font-size:19px\">2014</span><span style=\"font-size:19px;font-family:宋体\">年度优秀科技工作者</span></p><p style=\"text-indent:37px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:&#39;微软雅黑&#39;,sans-serif\">科研情况：</span></strong></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">1.&nbsp;</span><span style=\"font-size:19px;font-family:宋体\">工程决策的伦理规约，人民邮电出版社，</span><span style=\"font-size: 19px\">2014</span><span style=\"font-size:19px;font-family:宋体\">，专著</span><span style=\"font-size:19px\"> &nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">2. On Ethical Problems in Realistic Engineering Activity</span><span style=\"font-size:19px;font-family:宋体\">（工程活动中的伦理问题）</span><span style=\"font-size:19px\"> 13th&nbsp;International Congress of Logic,&nbsp;Methodology&nbsp;and Philo- sophy&nbsp;of Science, &nbsp;2011,College Publications, London, UK</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">3.</span><span style=\"font-size:19px;font-family: 宋体\">国际工程伦理的知识聚类及其前沿演进研究</span><span style=\"font-size:19px\">.</span><span style=\"font-size:19px;font-family:宋体\">东北大学学报</span><span style=\"font-size:19px\">(CSSCI</span><span style=\"font-size:19px;font-family:宋体\">源刊</span><span style=\"font-size:19px\">)2010.5 &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">4.&nbsp;On Media Model and Management&nbsp;Counter- measures of Tacit Knowledge</span><span style=\"font-size:19px;font-family: 宋体\">（隐性知识的介质模式及其管理对策研究）</span><span style=\"font-size:19px\"> International Conference on Computer Science and Service System,2011&nbsp;(EI</span><span style=\"font-size:19px;font-family:宋体\">检索</span><span style=\"font-size:19px\">)</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">5. On Engineering Ethics Education.&nbsp;</span><span style=\"font-size:19px;font-family:宋体\">（论工程伦理教育）</span><span style=\"font-size:19px\"> International Conference on Applied Social Science,&nbsp;2011(ISTP</span><span style=\"font-size:19px;font-family:宋体\">检索</span><span style=\"font-size:19px\">) </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">6.</span><span style=\"font-size:19px;font-family: 宋体\">马克思主义基本原理概论综合实践用书</span> <span style=\"font-size:19px;font-family: 宋体\">东北大学出版社，</span><span style=\"font-size:19px\">2014</span><span style=\"font-size:19px;font-family:宋体\">，参编</span><span style=\"font-size:19px\"> &nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">7.</span><span style=\"font-size:19px;font-family: 宋体\">毛泽东思想和中国特色社会主义理论体系概论综合实践指导用书</span> <span style=\"font-size:19px;font-family: 宋体\">东北大学出版社，</span><span style=\"font-size:19px\">2015</span><span style=\"font-size:19px;font-family:宋体\">，参编</span><span style=\"font-size:19px\"> &nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">8.</span><span style=\"font-size:19px;font-family: 宋体\">大学生思想政治教育综合实践指导用书</span> <span style=\"font-size:19px;font-family: 宋体\">东北大学出版社，</span><span style=\"font-size:19px\">2015</span><span style=\"font-size:19px;font-family:宋体\">，参编</span><span style=\"font-size:19px\"> &nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">9.</span><span style=\"font-size:19px;font-family: 宋体\">《毛泽东思想和中国特色社会主义理论体系概论》课程中情景教学模式的构建与应用研究</span> <span style=\"font-size:19px;font-family: 宋体\">情景剧教学法在高校思想政治教育中的理论与实践研究</span><span style=\"font-size:19px\">,</span><span style=\"font-size:19px;font-family:宋体\">沈阳出版社，</span><span style=\"font-size:19px\">2012&nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">10.</span><span style=\"font-size:19px;font-family: 宋体\">科学发展观视域下的高校校园文化建设研究</span> <span style=\"font-size:19px;font-family: 宋体\">在思考中前行</span><span style=\"font-size:19px\">,</span><span style=\"font-size:19px;font-family:宋体\">辽宁大学出版社，</span><span style=\"font-size:19px\">2013&nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">11.</span><span style=\"font-size:19px;font-family: 宋体\">漫画心理学，中国经济出版社，</span><span style=\"font-size:19px\">2014</span><span style=\"font-size:19px;font-family:宋体\">，主编</span><span style=\"font-size:19px\"> &nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">12.</span><span style=\"font-size:19px;font-family: 宋体\">做一个懂工作会休息善疏导的解压达人，中国电力出版社，</span><span style=\"font-size:19px\">2015</span><span style=\"font-size:19px;font-family:宋体\">，主编</span><span style=\"font-size:19px\">&nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">13.</span><span style=\"font-size:19px;font-family: 宋体\">管理沟通概论</span> <span style=\"font-size:19px;font-family: 宋体\">机械工业出版社</span><span style=\"font-size:19px\">2011</span><span style=\"font-size:19px;font-family:宋体\">，参编</span><span style=\"font-size:19px\"> &nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">14.</span><span style=\"font-size:19px;font-family: 宋体\">人力资源管理</span> <span style=\"font-size:19px;font-family: 宋体\">人民邮电出版社，</span><span style=\"font-size:19px\">2013</span><span style=\"font-size:19px;font-family:宋体\">，参编</span><span style=\"font-size:19px\"> &nbsp; &nbsp; </span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px\">15.&nbsp;Research on International Tacit Knowledge in Frontier Domains in Visualized Information</span><span style=\"font-size:19px;font-family:宋体\">（可视化视域下的隐性知识前沿研究）</span><span style=\"font-size: 19px\"> International Conference on Computer Science and Service System</span><span style=\"font-size:19px;font-family:宋体\">（</span><span style=\"font-size:19px\">EI</span><span style=\"font-size:19px;font-family: 宋体\">检索），</span><span style=\"font-size:19px\">2011&nbsp; &nbsp; </span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: &#39;微软雅黑&#39;,sans-serif\">其他突出事迹：</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size:19px;line-height:150%;font-family: 宋体\">参与辽宁省教育教学质量工程建设：《毛泽东思想和中国特色社会主义理论体系概论》省级资源共享课</span> <span style=\"font-size:19px;line-height:150%;font-family:宋体\">主讲其中</span><span style=\"font-size:19px;line-height:150%\">1/4</span><span style=\"font-size: 19px;line-height:150%;font-family:宋体\">的知识点，目前该课程已被</span><span style=\"font-size:19px;line-height:150%\">8</span><span style=\"font-size:19px;line-height:150%;font-family:宋体\">所同行院校选定为跨校选修课程。</span></p><p><br/></p>', 1513078065, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (298, '曲洪波', '管理员', '<p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\"><img src=\"/Public/Upload/image/20171212/1513078301700342.jpg\" title=\"1513078301700342.jpg\" alt=\"1513078301700342.jpg\" width=\"400\" height=\"\" border=\"0\" vspace=\"0\" style=\"width: 400px;\"/></span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">曲洪波，男，</span><span style=\"font-size:16px;line-height:150%\">1978</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:16px;line-height:150%\">8</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">月生，辽宁省抚顺人，中共党员，副教授，硕士研究生导师，副院长。</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">主讲课程：毛泽东思想和中国特色社会主义理论体系概论、中国近现代史纲要、当代世界经济与政治、中国近现代史重大问题研究、中国特色社会主义理论与实践</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所获荣誉：全国思想政治理论课年度影响力人物提名、全国思政课教学能手、辽宁省思政课年度影响力人物、辽宁省思政课优秀教师、沈阳市优秀教师、校教学名师，并评为沈阳航空航天大学“我最喜爱的教师”、研究生良师益友</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">科研情况：出版学术专著</span><span style=\"font-size:16px;line-height:150%\">2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">部，发表论文</span><span style=\"font-size:16px;line-height:150%\">40</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">余篇，主持教育部课题两项、辽宁省社科基金课题</span><span style=\"font-size:16px;line-height:150%\">2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">项、辽宁省教学改革课题一项，辽宁省教育厅课题、辽宁省社科联课题多项。</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%\">&nbsp;</span></p><p><br/></p>', 1513078240, 100, 5, 0);
INSERT INTO `tp_blogss` VALUES (285, '学院简介', 'admin', '<p style=\"text-align:center;text-indent:40px;line-height:29px\"><span style=\"font-family: 宋体; font-size: 19px; text-indent: 37px;\">马克思主义学院的学科组织机构可以追溯到20世纪50年代，历经马列主义教研室、社科系、社科部、人文社科部、思想政治理论课教学科研部。2014年12月，更名为马克思主义学院。2006年经国务院学位委员会批准设立了马克思主义理论一级学科硕士点。下设马克思主义基本原理、马克思主义中国化理论与实践、中国近代史基本问题研究、思想政治教育四个研究方向。学院现有在职员工27人，兼职教师15人，其中教授 7人、副教授18人、讲师16人，博士学位11人，硕博比率达到90%以上。</span><br/></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">马克思主义理论与思想政治教育学科点坚持引进与培养齐举，激励与规范并重，加强师德师风建设，优化学术团队建设，着力培育教师的教学艺术、学术水平和综合素质，提升学科在人才培养、科学研究、咨政服务和文化传承与创新中的贡献力。</span></p><p style=\"text-indent:37px;line-height:150%\"><span style=\"font-size: 19px;line-height: 150%;font-family: 宋体\">学校高度重视、积极投入马克思主义学院建设。师资队伍力量较强。思政课情景剧教学法荣获教育部首届思政课教学方法改革择优推广项目。思政课教学团队荣获2013年度辽宁省思政课优秀教学团队。拥有全国高校思想政治理论课教师2014年度影响力人物1人、全国高校思想政治理论课教师2015年度影响力提名人物1人；辽宁省教学名师3人、学校教学名师6人；2013和2014年辽宁省高校优秀思想政治理论课教师择优资助计划2人。经过长期不断的积累，我校马克思主义学院具有较强的教学研究和科学研究实力，学科建设已形成特色。&nbsp;</span></p><p><br/></p>', 1513055726, 1, 3, 2);
